import React from 'react';
import JoinForm from "../components/JoinForm";

const JoinPage = () => {
    return (
        <div className="flex h-screen">
            {/* 좌측: 회원가입 폼 */}
            <div className="w-1/2 flex flex-col justify-center items-center bg-neutral-50">
                <JoinForm/>
            </div>

            {/* 우측: 일러스트 영역 */}
            <div className="w-1/2 bg-[#EEE9E5] flex items-center justify-center">
                <img src="/img/join-illustration.png" alt="회원가입 일러스트" className="w-[80%] h-screen object-cover" />
            </div>
        </div>
    );
};

export default JoinPage;
