// src/features/auth/pages/LoginPage.jsx
import React, { useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';

const LoginPage = () => {
    const navigate = useNavigate();

    const [formData, setFormData] = useState({
        username: '', // ✅ DB 기준: 로그인용 ID
        password: '',
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prev) => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // ✅ 세션 기반 로그인
            await axios.post('http://localhost:8080/api/member/login', formData, {
                withCredentials: true, // ✅ 세션 유지하려면 반드시 필요
            });

            alert('로그인 성공!');
            navigate('/home'); // 로그인 성공 후 이동
        } catch (error) {
            alert('로그인 실패! 아이디 또는 비밀번호를 확인하세요.');
            console.error(error);
        }
    };

    return (
        <div className="flex h-screen w-screen items-center justify-center bg-[#EBEFFF]">
            {/* 왼쪽 영역 - 로그인 */}
            <div className="mb-[2rem] flex h-full flex-[47%] flex-col items-center justify-center px-12">
                <h2 className="mb-6 text-center text-2xl font-bold">환영합니다</h2>
                <form className="flex w-[80%] flex-col space-y-4" onSubmit={handleSubmit}>
                    <div>
                        <p className="mb-1 text-font">아이디</p>
                        <input
                            type="text"
                            name="username"
                            value={formData.username}
                            onChange={handleChange}
                            placeholder="아이디를 입력하세요"
                            className="w-full rounded border border-hover p-2 transition duration-200 hover:border-2 focus:border-2 focus:outline-none"
                            required
                        />
                    </div>

                    <div>
                        <p className="mb-1 text-font">비밀번호</p>
                        <input
                            type="password"
                            name="password"
                            value={formData.password}
                            onChange={handleChange}
                            placeholder="비밀번호"
                            className="mb-[2rem] w-full rounded border border-hover p-2 transition duration-200 hover:border-2 focus:border-2 focus:outline-none"
                            required
                        />
                    </div>

                    <div className="mt-[2rem] space-y-8">
                        <button type="submit" className="w-full rounded bg-[#405DF9] py-3 text-white">
                            로그인
                        </button>

                        <button className="mx-auto flex h-auto w-auto flex-col items-center border-none bg-transparent p-0">
                            <img
                                src={process.env.PUBLIC_URL + '/img/kakao_login.png'}
                                alt="카카오 로그인"
                                className="block"
                            />
                        </button>

                        <p className="text-center text-sm">
                            계정이 없으신가요?{' '}
                            <Link to="/join" className="text-blue-500">
                                회원가입
                            </Link>
                        </p>
                    </div>
                </form>
            </div>

            {/* 오른쪽 영역 - 이미지 */}
            <div className="relative flex h-full flex-[53%] justify-end">
                <div className="relative z-0 h-full w-[45rem] bg-indigo-300" />
                <img
                    className="absolute left-[calc(100%-45vw)] top-1/2 z-10 w-[30vw] -translate-y-1/2 sm:w-[28vw] md:w-[30vw] lg:w-[32vw] xl:w-[34vw]"
                    src={process.env.PUBLIC_URL + '/img/logIn.png'}
                    alt="가입 일러스트"
                />
            </div>
        </div>
    );
};

export default LoginPage;
