import React, {useState} from 'react';
import axios from 'axios';
import {Link, useNavigate} from 'react-router-dom';
import SocialLoginButtons from "./SocialLoginButtons";

const LoginForm = () => {
    const navigate = useNavigate();
    const [formData, setFormData] = useState({email: '', password: ''});

    const handleChange = (e) => {
        const {name, value} = e.target;
        setFormData((prev) => ({...prev, [name]: value}));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.post('http://localhost:8080/api/member/login', formData, {
                withCredentials: true,
            });
            alert('Î°úÍ∑∏Ïù∏ ÏÑ±Í≥µ!');
            navigate('/');
        } catch (error) {
            alert('Î°úÍ∑∏Ïù∏ Ïã§Ìå®!');
            console.error(error);
        }
    };

    return (
        <div className=" h-[90%] relative inline-flex flex-col justify-start items-start gap-8 font-sans text-font tracking-tight">
            {/*ÏÉÅÎã® ÌôòÏòÅ Î¨∏Íµ¨*/}
            <div className="flex flex-col justify-start items-start gap-3 text-left">
                <p className="text-4xl font-bold  leading-tight">ÌôòÏòÅÌï©ÎãàÎã§ üëã</p>
                <p className="text-xl font-bold  leading-snug"> Ïò§Îäò, ÎãπÏã†Ïùò Ïû¨Îä•Ïù¥ Ïó∞Í≤∞ÎêòÎäî ÏàúÍ∞Ñ.</p>
                <p className="text-base font-medium  leading-snug">
                    ÏùºÏÉÅÏóê ÏâºÏùÑ, Ïû¨Îä•Ïóê Í∞ÄÏπòÎ•º, ÏÇ¨ÎûåÏóê Ï≤†ÌïôÏùÑ ÏûáÎäî ITDAÏôÄ Ìï®Íªò
                    ÏßÄÍ∏à Î°úÍ∑∏Ïù∏ÌïòÍ≥† ÎãπÏã†Ïùò Ïù¥ÏïºÍ∏∞Î•º ÏãúÏûëÌïòÏÑ∏Ïöî.
                </p>
            </div>
            {/*Î°úÍ∑∏Ïù∏ÌïÑÎìú*/}
            <form className="self-stretch flex flex-col justify-center items-end gap-6" onSubmit={handleSubmit}>
                {[
                    { label: "Email", name: "email", type: "text", placeholder: "example@itda.com" },
                    { label: "Password", name: "password", type: "password", placeholder: "ÎπÑÎ∞ÄÎ≤àÌò∏" },
                ].map(({ label, name, type, placeholder }) => (
                    <div key={name} className="self-stretch flex flex-col justify-start items-start gap-2">
                        <p>{label}</p>
                        <input
                            type={type}
                            name={name}
                            value={formData[name]}
                            onChange={handleChange}
                            placeholder={placeholder}
                            className="w-96 h-12 bg-slate-50 rounded-xl outline outline-1 outline-gray-300 border hover:border-hover p-2"
                            required
                        />
                    </div>
                ))}

                {/*ÎπÑÎ∞ÄÎ≤àÌò∏ Ï∞æÍ∏∞*/}
                <div
                    className="text-center justify-center text-blue-700 text-[0.8rem] font-normal leading-none ">
                    Forgot Password?
                </div>
                {/*Î°úÍ∑∏Ïù∏Î≤ÑÌäº*/}
                <button type="submit" className="self-stretch flex justify-center bg-slate-800 rounded-xl py-3 text-white text-lg">
                    Sign in
                </button>
            </form>

            {/*Îã§Î•∏Î∞©Î≤ïÏúºÎ°ú Î°úÍ∑∏Ïù∏*/}
            <div className="flex flex-col justify-start items-start gap-6">
                <div className="w-96 flex items-center gap-4">
                    <hr className="flex-1 border-t border-slate-300" />
                    <span className="text-base text-gray-500">Or</span>
                    <hr className="flex-1 border-t border-slate-300" />
                </div>
                <SocialLoginButtons />
            </div>
            <div className="w-full text-center justify-center text-base font-normal leading-7 ">
                Í≥ÑÏ†ïÏù¥ ÏóÜÏúºÏã†Í∞ÄÏöî?  <Link to="/join" className="text-blue-700 hover:underline">ÌöåÏõêÍ∞ÄÏûÖ</Link>
            </div>
            <div className="w-full text-center text-slate-400">¬© 2025 ITDA</div>
        </div>
    );
};

export default LoginForm;
