// src/features/auth/pages/LoginPage.jsx
import React, { useState } from 'react';
import { loginUser } from '../api';
import PasswordInput from '../components/PasswordInput';

const LoginPage = () => {
    const [formData, setFormData] = useState({
        username: '',
        password: '',
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prev) => ({ ...prev, [name]: value }));
    };

    const handlePasswordChange = (value) => {
        setFormData((prev) => ({ ...prev, password: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const res = await loginUser(formData);
            console.log('로그인 성공:', res.data);
            // 필요 시 localStorage.setItem(), navigate 등 추가
        } catch (error) {
            console.error('로그인 실패:', error);
        }
    };

    return (
        <div className="flex h-screen w-screen items-center justify-center bg-[#F5F6FE]">
            <div className="w-full max-w-md rounded-xl bg-white p-8 shadow-md">
                <h2 className="mb-6 text-center text-2xl font-bold">로그인</h2>
                <form className="space-y-4" onSubmit={handleSubmit}>
                    <div>
                        <label className="mb-1 block text-font">아이디</label>
                        <input
                            type="text"
                            name="username"
                            value={formData.username}
                            onChange={handleChange}
                            placeholder="아이디를 입력하세요"
                            className="w-full rounded border border-hover p-2 focus:outline-none focus:ring-2 focus:ring-indigo-300"
                            required
                        />
                    </div>

                    <PasswordInput onChange={handlePasswordChange} />

                    <button type="submit" className="w-full rounded bg-[#405DF9] py-2 text-white">
                        로그인
                    </button>
                </form>

                <p className="mt-4 text-center text-sm">
                    아직 계정이 없으신가요?{' '}
                    <a href="/join" className="text-blue-500 hover:underline">
                        회원가입
                    </a>
                </p>
            </div>
        </div>
    );
};

export default LoginPage;
