import React, {useEffect, useState} from "react";
import ClassCard from "../components/ClassCard"; // ClassCard Ïª¥Ìè¨ÎÑåÌä∏ ÏûÑÌè¨Ìä∏


const banners = [//Î∞∞ÎÑà 2Ïû•ÏùÑ Îã¥ÏïÑ Îëò Î∞∞Ïó¥
    "/img/Benner1.svg", "/img/Benner2.svg"];


const classes = [{
    image: "/img/Class_image_1.jpg", title: "Í∑ÄÏóºÎΩÄÏßù Ìó§.Íæ∏  ‚òÜ*o(‚âß‚ñΩ‚â¶)o*‚òÜ", instructor: "Í∞ïÎ≥¥ÏùÄ", people: 3  // Ïà´ÏûêÎ°ú Î≥ÄÍ≤Ω
}, {
    image: "/img/Class_image_2.jpg", title: "Í∞ÄÏßÄÍ∞ÅÏÉâ ÌíçÏÑ† ÍµêÏã§üéà", instructor: "ÏµúÏû¨ÌòÑ", people: 0
}, {
    image: "/img/Class_image_3.jpg", title: "Ï∫îÎ≤ÑÏä§ÏôÄ Ïú†ÌôîÏùò Ïù¥ÏïºÍ∏∞", instructor: "Í∞ïÎã§Ïó∞", people: 5
}

];

const classes2 = [{
    image: "/img/Class_image_4.jpg", title: "Ïª¥Ìì®ÌÑ∞Ï†Å ÏÇ¨Í≥†ÌïòÎäî Î∞©Î≤ï", instructor: "Î∞±ÏäπÎ≤î", people: 5
}, {
    image: "/img/Class_image_5.jpg", title: "Ï§ëÎì±ÏàòÌïô Í∞ôÏù¥ Î∞∞ÏõåÎ¥êÏöîœÜ(*Ôø£0Ôø£)", instructor: "Ï†ïÏäπÏ†ú", people: 5
}, {
    image: "/img/Class_image_6.jpg", title: "Ïä§ÌîÑÎßÅÎ∂ÄÌä∏ ÌååÌó§ÏπòÍ∏∞", instructor: "Ï°∞ÎèôÏùº", people: 15
}];


function MainPage() {

    const [currentIndex, setCurrentIndex] = useState(0);//ÌòÑÏû¨ ÏÉÅÌÉúÎ•º ÎÇòÌÉÄÎÇ¥Îäî ÏÉÅÏàò

    useEffect(() => {
        const interval = setInterval(() => {
            setCurrentIndex((prev) => (prev + 1) % banners.length);
        }, 6000);
        return () => clearInterval(interval);
    }, []);

    return (<div>
            {/* Î©îÏù∏ÏΩòÌÖêÏ∏† */}
            <main className="flex-grow overflow-hidden relative bg-white">
                <div
                    className="flex transition-transform duration-700 ease-in-out"
                    style={{transform: `translateX(-${currentIndex * 100}%)`}}
                >
                    {banners.map((src, index) => (
                        <div key={index} className="w-full flex-shrink-0 flex justify-center items-center bg-white">
                            <img
                                src={src}
                                alt={`Î∞∞ÎÑà ${index}`}
                                className="object-contain max-h-[500px] w-full bg-white"
                            />
                        </div>
                    ))}
                </div>


                {/* Î∞∞ÎÑà ÏïÑÎûò ÎÑ§Î™® Î≤ÑÌäº 4Í∞ú */}
                <div className="flex justify-center items-center space-x-14 my-6 pt-10">
                    {/* Ï≤´ Î≤àÏß∏ Î≤ÑÌäº - ÏßÄÏó≠ */}
                    <div
                        className="active:scale-95 w-[220px] h-20 bg-white hover:bg-gray-300 rounded-md flex items-center px-4 space-x-4 shadow-md hover:shadow-lg hover:scale-105 transition duration-300">
                        {/* ÏïÑÏù¥ÏΩò */}
                        <img src="/img/Local_icon.png" alt="ÏßÄÏó≠ ÏïÑÏù¥ÏΩò" className="w-14 h-14"/>

                        {/* ÌÖçÏä§Ìä∏ Î¨∂Ïùå */}
                        <div className="h-20 flex flex-col justify-center">
                            <span className="text-[16px] font-pretendard font-semibold leading-none ml-10">ÏßÄÏó≠</span>
                            <h1 className="text-[10px] font-pretendard text-gray-600 mt-[10px] leading-none">ÏßÄÏó≠Î≥Ñ ÌÅ¥ÎûòÏä§Î•º
                                ÎëòÎü¨Î≥¥Í∏∞</h1>
                        </div>
                    </div>


                    {/* Îëê Î≤àÏß∏ Î≤ÑÌäº ÏòàÏãú */}
                    <div
                        className="active:scale-95 w-[220px] h-20 bg-white hover:bg-gray-300 rounded-md flex items-center px-4 space-x-4 shadow-md hover:shadow-lg hover:scale-105 transition duration-300">

                        <img src="/img/Type_icon.png" alt="Ïú†Ìòï ÏïÑÏù¥ÏΩò" className="w-14 h-14"/>
                        {/* ÌÖçÏä§Ìä∏ Î¨∂Ïùå */}
                        <div className="h-20 flex flex-col justify-center">
                            <span className="text-[16px] font-pretendard font-semibold leading-none ml-10">Ïú†Ìòï</span>
                            <h1 className="text-[10px] font-pretendard text-gray-600 mt-[10px] leading-none">Ïú†ÌòïÎ≥Ñ ÌÅ¥ÎûòÏä§Î•º
                                ÎëòÎü¨Î≥¥Í∏∞</h1>
                        </div>
                    </div>


                    {/* ÏÑ∏ Î≤àÏß∏ Î≤ÑÌäº ÏòàÏãú */}
                    <div
                        className="active:scale-95 w-[220px] h-20 bg-white hover:bg-gray-300 rounded-md flex items-center px-4 space-x-4 shadow-md hover:shadow-lg hover:scale-105 transition duration-300">

                        <img src="/img/Category_icon.png" alt="Ïπ¥ÌÖåÍ≥†Î¶¨ ÏïÑÏù¥ÏΩò" className="w-14 h-14"/>
                        <div className="h-20 flex flex-col justify-center">
                            <span className="text-[16px] font-pretendard font-semibold leading-none ml-5">Ïπ¥ÌÖåÍ≥†Î¶¨</span>
                            <h1 className="text-[10px] font-pretendard text-gray-600 mt-[10px] leading-none">Ïπ¥ÌÖåÍ≥†Î¶¨Î≥Ñ Îã§ÏñëÌïú
                                ÌÅ¥ÎûòÏä§</h1>
                        </div>
                    </div>

                    {/* ÎÑ§ Î≤àÏß∏ Î≤ÑÌäº ÏòàÏãú */}
                    <div
                        className="active:scale-95 w-[220px] h-20 bg-white hover:bg-gray-300 rounded-md flex items-center px-4 space-x-4 shadow-md hover:shadow-lg hover:scale-105 transition duration-300">

                        <img src="/img/Level_icon.png" alt="ÎÇúÏù¥ÎèÑ ÏïÑÏù¥ÏΩò" className="w-14 h-14"/>
                        <div className="h-20 flex flex-col justify-center">
                            <span className="text-[16px] font-pretendard font-semibold leading-none ml-8">ÎÇúÏù¥ÎèÑ</span>
                            <h1 className="text-[10px] font-pretendard text-gray-600 mt-[10px] leading-none">ÎÇúÏù¥ÎèÑÎ≥Ñ ÌÅ¥ÎûòÏä§Î•º
                                ÎëòÎü¨Î≥¥Í∏∞</h1>
                        </div>
                    </div>
                </div>

                {/*ÌÅ¥ÎûòÏä§ Í¥ÄÎ†® ÏÑπÏÖò*/}
                <section className="py-10 flex justify-center">
                    <div className="w-full max-w-6xl px-4">
                        <h2 className="text-xl font-bold text-gray-700 mb-6">
                            üíõ Î™ΩÍ∏ÄÎ™ΩÍ∏Ä ‚åàÍ∞êÏÑ±Ï∂©Îßå‚åã ÌÅ¥ÎûòÏä§Îì§ÏùÄ Ïñ¥ÎïåÏöî? üíõ
                        </h2>
                        <div className="flex flex-wrap gap-10">
                            {classes.map((classInfo, index) => (
                                <ClassCard
                                    key={index}
                                    image={classInfo.image}
                                    title={classInfo.title}
                                    instructor={classInfo.instructor}
                                    people={classInfo.people}
                                />
                            ))}
                        </div>
                    </div>
                </section>


                {/*ÌÅ¥ÎûòÏä§ Í¥ÄÎ†® ÏÑπÏÖò*/}
                <section className="py-10 flex justify-center">
                    <div className="w-full max-w-6xl px-4">
                        <h2 className="text-xl font-bold text-gray-700 mb-6">
                            Ïò§ÎäòÏùÄ Ïô†ÏßÄ Î®∏Î¶¨ Ïì∞Í≥† Ïã∂ÏùÄ ÎÇ†Ïù¥ÎÑ§...ü§îü§î
                        </h2>
                        <div className="flex flex-wrap gap-10">
                            {classes2.map((classInfo, index) => (<ClassCard
                                key={index}
                                image={classInfo.image}
                                title={classInfo.title}
                                instructor={classInfo.instructor}
                                people={classInfo.people}
                            />))}
                        </div>
                    </div>
                </section>
            </main>
        </div>
    );
}

export default MainPage;
