import React, { useState } from "react";

console.log("üü¢ PostForm Î†åÎçîÎßÅÎê®");


function PostForm({ onSubmit, showTags = false }) {
    const [title, setTitle] = useState("");
    const [content, setContent] = useState("");
    const [tags, setTags] = useState([]);

    const availableTags = ["HTML", "CSS", "JavaScript", "React", "Í∏∞ÌÉÄ"];

    const toggleTag = (tag) => {
        setTags((prev) =>
            prev.includes(tag) ? prev.filter((t) => t !== tag) : [...prev, tag]
        );
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!title || !content) {
            alert("Ï†úÎ™©Í≥º ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.");
            return;
        }
        onSubmit({ title, content, tags });
        setTitle("");
        setContent("");
        setTags([]);
    };

    return (
        <form onSubmit={handleSubmit} className="space-y-6">
            {/* Ï†úÎ™© */}
            <div>
                <label className="block text-gray-700 font-medium mb-1">Ï†úÎ™©</label>
                <input
                    type="text"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    placeholder="Ï†úÎ™©ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
                    className="w-full border border-gray-300 rounded-md px-4 py-2 focus:outline-none focus:ring-2 focus:ring-[#3D4EFE]"
                />
            </div>

            {/* ÎÇ¥Ïö© */}
            <div>
                <label className="block text-gray-700 font-medium mb-1">ÎÇ¥Ïö©</label>
                <textarea
                    value={content}
                    onChange={(e) => setContent(e.target.value)}
                    placeholder="ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
                    rows="8"
                    className="w-full border border-gray-300 rounded-md px-4 py-2 focus:outline-none focus:ring-2 focus:ring-[#3D4EFE]"
                />
            </div>

            {/* ÌÉúÍ∑∏ (ÏÑ†ÌÉù) */}
            {showTags && (
                <div>
                    <label className="block text-gray-700 font-medium mb-2">ÌÉúÍ∑∏</label>
                    <div className="flex flex-wrap gap-2">
                        {availableTags.map((tag) => (
                            <button
                                type="button"
                                key={tag}
                                onClick={() => toggleTag(tag)}
                                className={`px-3 py-1 rounded-full border text-sm font-medium transition ${
                                    tags.includes(tag)
                                        ? "bg-[#3D4EFE] text-white border-[#3D4EFE]"
                                        : "bg-white text-gray-700 border-gray-300 hover:border-[#3D4EFE]"
                                }`}
                            >
                                {tag}
                            </button>
                        ))}
                    </div>
                </div>
            )}

            {/* Ï†úÏ∂ú */}
            <div className="text-right">
                <button
                    type="submit"
                    className="bg-[#3D4EFE] text-white px-6 py-2 rounded-md font-semibold hover:bg-[#2c3ed9]"
                >
                    Îì±Î°ù
                </button>
            </div>
        </form>
    );
}

export default PostForm;
