import React, { useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import SocialLoginButtons from "./SocialLoginButtons";

const JoinForm = () => {
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        username: '',   // 실명
        nickname: '',   // 별명
        email: '',
        password: '',
        confirmPassword: '',
        phone: '',
        gender: '',
        role: 'MENTEE',
        loginType: 'LOCAL',
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prev) => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (formData.password !== formData.confirmPassword) {
            alert("비밀번호가 일치하지 않습니다.");
            return;
        }

        try {
            await axios.post('http://localhost:8080/api/member/join', formData);
            alert('회원가입 성공!');
            navigate('/login');
        } catch (error) {
            alert('회원가입 실패!');
            console.error(error);
        }
    };

    return (
        <div className="w-96 relative inline-flex flex-col justify-start items-start gap-12 font-sans text-font tracking-tight">
            {/*상단 환영 문구*/}
            <div className="flex flex-col justify-start items-start gap-3 text-left">
                <p className="text-4xl font-bold  leading-tight">시작해 볼까요? 🚀</p>
                <p className="text-xl font-bold  leading-snug"> 오늘, 당신의 재능이 연결되는 시작점.</p>
                <p className="text-base font-medium  leading-snug">
                    당신의 재능과 이야기를 나눌 준비가 되셨다면,
                    지금 가입하고 ITDA와 함께 새로운 여정을 시작하세요.
                </p>
            </div>

            {/* 회원가입 폼 */}
            <form className="self-stretch flex flex-col justify-center items-end gap-6" onSubmit={handleSubmit}>
                {[
                    { label: "이름", name: "username", type: "text", placeholder: "홍길동" },
                    { label: "닉네임", name: "nickname", type: "text", placeholder: "별명" },
                    { label: "Email", name: "email", type: "email", placeholder: "example@itda.com" },
                    { label: "비밀번호", name: "password", type: "password", placeholder: "비밀번호" },
                    { label: "비밀번호 확인", name: "confirmPassword", type: "password", placeholder: "비밀번호 확인" },
                ].map(({ label, name, type, placeholder }) => (
                    <div key={name} className="self-stretch flex flex-col justify-start items-start gap-2">
                        <p>{label}</p>
                        <input
                            type={type}
                            name={name}
                            value={formData[name]}
                            onChange={handleChange}
                            placeholder={placeholder}
                            className="w-96 h-12 bg-slate-50 rounded-xl outline outline-1 outline-gray-300 border hover:border-hover p-2"
                            required
                        />
                    </div>
                ))}

                {/* 회원가입 버튼 */}
                <button type="submit" className="self-stretch flex justify-center bg-slate-800 rounded-xl py-3 text-white text-lg">
                    회원가입
                </button>
            </form>

            {/* 소셜 로그인 */}
            <div className="flex flex-col justify-start items-start gap-6">
                <div className="w-96 flex items-center gap-4">
                    <hr className="flex-1 border-t border-slate-300" />
                    <span className="text-base text-gray-500">Or</span>
                    <hr className="flex-1 border-t border-slate-300" />
                </div>
                <SocialLoginButtons />
            </div>

            {/* 로그인 링크 */}
            <div className="w-full text-center text-base font-normal leading-7">
                이미 계정이 있으신가요?{" "}
                <Link to="/login" className="text-blue-700 hover:underline">
                    로그인
                </Link>
            </div>

            <div className="w-full text-center text-slate-400">© 2025 ITDA</div>
        </div>
    );
};

export default JoinForm;
